#include <bits/stdc++.h>

using namespace std;

char s[] = "";

int main() {
  //cerr << strlen(s) << endl;
  assert(strlen(s) == 1023);
  int n;
  scanf("%d", &n);
  if (n > strlen(s)) {
    for (int i = 0; i < n; i++) printf("0");
    return 0;
  }
  for (int i = 0; i < n; i++) printf("%c", s[i]);
  return 0;
}
